import React from 'react';
import './login.css';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "Priyankabauskar",
      password: "abc@123"
    };
    this.updatedusername = this.updatedusername.bind(this);
    this.updatedpassword = this.updatedpassword.bind(this);
  }
  updatedusername(e) {
    this.setState({ newusername: e.target.value });
  }
  updatedpassword(e) {
    this.setState({ newpassword: e.target.value });
  }

  handleValidation =() =>{
    let username = this.state.newusername; 
    let password = this.state.newpassword;

    if (!(username.match(/^([A-Za-z0-9_\-\.])+\@([A-Za-z0-9_\-\.])+\.([A-Za-z]{2,4})$/)) || username === "") 
    {
      alert("Please enter valid username");
    }
    if (( password === "") )
    {
      alert("Please enter valid password");
    }
    this.apiAuthentication();
  }

}
const login = (props) => {

    return(
        <div id="container">
        <label htmlFor="uname">
          <b>Username :</b>
        </label>
        <input
          type="text"
          placeholder="Enter Username"
          name="uname"
          onChange={event=> props.userHandler(event)}
        />

        <label htmlFor="psw">
          <b>Password :</b>
        </label>
        <input
          type="password"
          placeholder="Enter Password"
          name="psw"
          onChange={event => props.passwordHandler(event)}
        />

        <button type="submit" onClick={props.click}>
          Login
        </button>
      </div>
    );
}

export default login;
