import React from 'react';
import './App.css';
import Login from './login/login';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "Priyankabauskar",
      password: "abc@123"
    };
    this.updatedusername = this.updatedusername.bind(this);
    this.updatedpassword = this.updatedpassword.bind(this);
  }
  updatedusername(e) {
    this.setState({ newusername: e.target.value });
  }
  updatedpassword(e) {
    this.setState({ newpassword: e.target.value });
  }
  componentDidMount(){
    axios.get('https://randomuser.me/api/?results=10&inc=name,registered&nat=fr')
    .then(json => console.log(json))
  }
  validation = () => {
    console.log( this.state.newusername);
    if (
      this.state.username === this.state.newusername &&
      this.state.password === this.state.newpassword
    ) {
      alert("success");
    } else {
      alert("fail");
    }
  };

  render() {
    return (
      <div className="App">
       <Login 
       click={this.validation}/>
      </div>
    );
  }
}
export default App;
