import './App.css';

class App extends React.Component {
  constructor(props) {
     super(props);

     this.state = {
        Username: "Priyankabauskar",
        password: "abc@123"
     }
     this.updatedUsername = this.updatedUsername.bind(this);
     this.updatedpassword = this.updatedpassword.bind(this);

  };
  updatedUsername(e) {
    this.setState({data: e.target.value});
 }
 updatedpassword(e) {
  this.setState({data: e.target.value});
}
validation = () => {
  if(this.state.Username === this.updatedUsername && this.state.password === this.updatedpassword)
  {
    alert("success");

  }
  else{
    alert("fail");

  }

}

  render() {
     return (
       <div className="App">

              <label htmlFor="uname"><b>Username :</b></label>
              <input type="text" placeholder="Enter Username" name="uname"   onChange = {this.updatedUsername}  />

              <label htmlFor="psw"><b>Password :</b></label>
              <input type="password" placeholder="Enter Password" name="psw" onChange = {this.updatedpassword}   />

              <button type="submit" onClick={this.validation}>Login</button>

       </div>
     );
  }
}
export default App;
