import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      Username: "Priyankabauskar",
      password: "abc@123"
    };
    // this.updatedUsername = this.updatedUsername.bind(this);
    // this.updatedpassword = this.updatedpassword.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }
  // updatedUsername(e) {
  //   this.setState({ _username: e.target.value });
  // }
  // updatedpassword(e) {
  //   this.setState({ _password: e.target.value });
  // }
  handleChange(){
    this.setState({ [e.target.name]: e.target.value });
  }
  validation = () => {
    if (
      this.state.Username === this.state._username &&
      this.state.password === this.state._password
    ) {
      alert("success");
    } else {
      alert("fail");
    }
  };

  render() {
    return (
      <div className="App">
        <div id="container">
          <label htmlFor="uname">
            <b>Username :</b>
          </label>
          <input
            type="text"
            placeholder="Enter Username"
            name="uname"
            onChange={this.handleChange}
          />

          <label htmlFor="psw">
            <b>Password :</b>
          </label>
          <input
            type="password"
            placeholder="Enter Password"
            name="psw"
            onChange={this.handleChange}
          />

          <button type="submit" onClick={this.validation}>
            Login
          </button>
        </div>
      </div>
    );
  }
}
export default App;
