import './App.css';

class App extends React.Component {
  constructor(props) {
     super(props);

     this.state = {
        Username: "",
        password: ""
     }
     this.updatedUsername = this.updatedUsername.bind(this);
     this.updatedpassword = this.updatedpassword.bind(this);

  };
  updatedUsername(e) {
    this.setState({ _username: e.target.value });
 }
 updatedpassword(e) {
  this.setState({ _password: e.target.value });
}

validation = () => {
  if ( this.state.Username === this.state._username &&  this.state.password === this.state._password )
  {
    alert("success");

  }
  else{
    alert("fail");

  }

}

  render() {

    return (
       <div className="App">

              <label htmlFor="uname"><b>Username :</b></label>
              <input type="text" placeholder="Enter Username" name="uname"  value={Username}  onChange = {this.updatedUsername}  />

              <label htmlFor="psw"><b>Password :</b></label>
              <input type="password" placeholder="Enter Password" name="psw"  value={password} onChange = {this.updatedpassword}   />

              <button type="submit" onClick={this.validation}>Login</button>

       </div>
     );
  }
}
export default App;
