import React, { useState } from "react";

import "./toast.css";

const ToastMessage = ({ message }) => {
  const [show, setShow] = useState(true);

  setTimeout(() => {
    setShow(false);
  }, 5000);
  return show ? <div className="toastContainer">{message}</div> : "";
};

export default ToastMessage;
