import React from "react";
import axios from 'axios';
import "./login.css";

class Login extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      Username: "Priyankabauskar",
      password: "abc@123"
    };
    this.updatedUsername = this.updatedUsername.bind(this);
    this.updatedpassword = this.updatedpassword.bind(this);
  }
  updatedUsername(e) {
    this.setState({ _username: e.target.value });
  }
  updatedpassword(e) {
    this.setState({ _password: e.target.value });
  }
  apiAuthentication =() =>{
      axios({
        method: 'post',
        url: 'https://dev-bepsy-api.objectedge.com/oe_commerce_api/occ/v1/oauth/login',
        data: {
              username :this.state.newusername, //trupti.kashid@objectedge.com
              password : this.state.newpassword //Objectedge$10
            },
        config: { headers: { 'authorization':'Bearer YWRtaW46YWRtaW4=',
            'content-type':'application/json' }}
        })
        .then(function (response) {
            //handle success
           console.log(response);
           console.log("SUCCESS");
        })
        .catch(function (response) {
            //handle error
           console.log("Failed");
        });

      } 
  validation = () => {
    if (
      this.state.Username === this.state._username &&
      this.state.password === this.state._password
    ) {
      alert("success");
    } else {
      alert("fail");
    }
  };

  render() {
    return (
      <div className="App">
        <div id="container">
          <label htmlFor="uname">
            <b>Username :</b>
          </label>
          <input
            type="text"
            placeholder="Enter Username"
            name="uname"
            onChange={this.updatedUsername}
          />

          <label htmlFor="psw">
            <b>Password :</b>
          </label>
          <input
            type="password"
            placeholder="Enter Password"
            name="psw"
            onChange={this.updatedpassword}
          />

          <button type="submit" onClick={this.validation}>
            Login
          </button>
        </div>
      </div>
    );
  }
}
export default Login;
